import React, { useState } from "react";

const NameForm = () => {
  const [point_gaurd, setName1] = useState("");
  const [shooting_guard, setName2] = useState("");
  const [small_forward, setName3] = useState("");
  const [power_forward, setName4] = useState("");
  const [center, setName5] = useState("");
  const [formError, setFormError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form validation
    if (
      !point_gaurd ||
      !shooting_guard ||
      !small_forward ||
      !power_forward ||
      !center
    ) {
      setFormError("Please complete all fields.");
      return;
    }

    // Reset form error
    setFormError("");

    // You can access the entered names in the state variables (name1, name2, etc.)
    console.log(point_gaurd);
    console.log(shooting_guard);
    console.log(small_forward);
    console.log(power_forward);
    console.log(center);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Point Guard"
          value={name1}
          onChange={(e) => setName1(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Shooting Guard"
          value={name2}
          onChange={(e) => setName2(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Small Forward"
          value={name3}
          onChange={(e) => setName3(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Power Forward"
          value={name4}
          onChange={(e) => setName4(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Center"
          value={name5}
          onChange={(e) => setName5(e.target.value)}
        />
      </div>

      {formError && <p className="error-message">{formError}</p>}

      <button type="submit" className="submit-button">
        Submit
      </button>
    </form>
  );
};

export default NameForm;
