import axios from "axios";
import NameForm from "./NameInput";
import ReloadButton from "./ReloadButton";
import React, { useState } from "react";
import "./index.css";

function App() {
  const [responseData, setResponseData] = useState(null);
  const [averages, setAverages] = useState(null);

  const handleSubmit = async (inputValue) => {
    try {
      console.log(inputValue);

      let player1 = inputValue[0].replace(/\s+/g, "-");
      let player2 = inputValue[1].replace(/\s+/g, "-");
      let player3 = inputValue[2].replace(/\s+/g, "-");
      let player4 = inputValue[3].replace(/\s+/g, "-");
      let player5 = inputValue[4].replace(/\s+/g, "-");

      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?pg=${player1}&sg=${player2}&sf=${player3}&pf=${player4}&c=${player5}`
      );

      for (let i = 0; i < 5; i++) {
        const name = response.data[i]["full_name"]["S"].replace(/-/g, " ");
        if (name === inputValue[0]) {
          player1 = response.data[i];
        } else if (name === inputValue[1]) {
          player2 = response.data[i];
        } else if (name === inputValue[2]) {
          player3 = response.data[i];
        } else if (name === inputValue[3]) {
          player4 = response.data[i];
        } else if (name === inputValue[4]) {
          player5 = response.data[i];
        }
      }

      const result = [player1, player2, player3, player4, player5];

      let avgFg = 0;
      let avgThree = 0;
      let avgFree = 0;

      for (let player in result) {
        avgFg += player["percentageFieldGoal"]["N"];
        avgThree += player["percentageThree"]["N"];
        avgFree += player["percentageFreethrow"]["N"];
      }

      avgFg /= 5;
      avgThree /= 5;
      avgFree /= 5;

      const avgs = [avgFg, avgThree, avgFree];

      setAverages(avgs);

      setResponseData(result);
    } catch (error) {
      // Handle error
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="court">
      <h1 className="title">NBA Lineup Grader</h1>
      {responseData ? (
        <ReloadButton></ReloadButton>
      ) : (
        <div className="instructions">
          <h1>NBA Lineup Grader</h1>
          <h2>Enter a starting five:</h2>
          <NameForm onSubmit={handleSubmit} />
        </div>
      )}
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[4]["headShotUrl"]["S"]}
            className="post-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData[3]["headShotUrl"]["S"]}
            className="post-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[2]["headShotUrl"]["S"]}
            className="wing-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData[1]["headShotUrl"]["S"]}
            className="wing-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[0]["headShotUrl"]["S"]}
            className="point-img"
            alt="headshot"
          />
        )}
      </span>
    </div>
  );
}

export default App;
