import axios from "axios";
import TextInput from "./TextInput";
import React, { useState } from "react"; // Import useState hook
import "./index.css";

function App() {
  const [responseData, setResponseData] = useState(null); // Declare state using useState hook

  const handleSubmit = async (inputValue) => {
    try {
      // Make API request with input value
      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?name=${inputValue}`
      );
      // Handle response
      console.log(response.data);

      const points = response.data["points"]["N"];
      const headshot = response.data["headShotUrl"]["S"];
      const result = {
        points: points,
        headshot: headshot,
      };
      setResponseData(result);
    } catch (error) {
      // Handle error
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="court">
      <h1 className="title">NBA Lineup Grader</h1>
      {responseData ? (
        <h1>HELLO</h1>
      ) : (
        <div className="instructions">
          <h1>Welcome to NBA Lineup Grader</h1>
          <h2>Instructions:</h2>
          <TextInput onSubmit={handleSubmit} />
        </div>
      )}
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="post-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData.headshot}
            className="post-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="wing-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData.headshot}
            className="wing-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="point-img"
            alt="headshot"
          />
        )}
      </span>
    </div>
  );
}

export default App;
