import axios from "axios";
import TextInput from "./TextInput";
import React, { useState } from "react";

function App() {
  const [responseData, setResponseData] = useState(null); // State to store response data
  const [error, setError] = useState(null); // State to store error

  const handleSubmit = async (inputValue) => {
    try {
      // Make API request with input value
      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?name=${inputValue}`
      );
      // Update state with response data
      setResponseData(response.data);
      setError(null); // Reset error state
    } catch (error) {
      // Update error state
      setError("Error fetching data");
      setResponseData(null); // Reset response data state
    }
  };

  return (
    <div className="main">
      <h1>NBA Lineup Grader</h1>
      <TextInput onSubmit={handleSubmit} />
      {error && <p>{error}</p>} {/* Display error message */}
      {responseData && (
        <div>
          <h2>Player Stats:</h2>
          <p>Player Name: {responseData.full_name}</p>
          <p>Points: {responseData.points}</p>
          <p>Assists: {responseData.assists}</p>
          <p>Rebounds: {responseData.rebounds}</p>
          <p>Turnovers: {responseData.turnovers}</p>
          <p>Field Goal Percentage: {responseData.percentageFieldGoal}</p>
          <p>Three-Point Percentage: {responseData.percentageThree}</p>
          <p>Free Throw Percentage: {responseData.percentageFreethrow}</p>
          {/* Render other data from the response */}
        </div>
      )}
    </div>
  );
}

export default App;
