import axios from "axios";
import TextInput from "./TextInput";
import React, { useState } from "react"; // Import useState hook

function App() {
  const [responseData, setResponseData] = useState(null); // Declare state using useState hook

  const handleSubmit = async (inputValue) => {
    try {
      // Make API request with input value
      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?name=${inputValue}`
      );
      // Handle response
      console.log(response.data);

      const points = response.data["points"]["N"];
      const headshot = response.data["headShotUrl"]["S"];
      const result = {
        points: points,
        headshot: headshot,
      };
      setResponseData(result);
    } catch (error) {
      // Handle error
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="main">
      <h1>NBA Lineup Grader</h1>
      <TextInput onSubmit={handleSubmit} />
      {responseData && <img src={responseData.headshot} />}
    </div>
  );
}

export default App;
