import axios from "axios";
import NameForm from "./NameInput";
import React, { useState } from "react"; // Import useState hook
import "./index.css";

function App() {
  const [responseData, setResponseData] = useState(null); // Declare state using useState hook

  const handleSubmit = async (inputValue) => {
    try {
      // Make API request with input value
      console.log(inputValue);

      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?pg=${inputValue[0]}&sg=${inputValue[1]}&sf=${inputValue[2]}&pf=${inputValue[3]}&c=${inputValue[4]}`
      );

      player1 = response.data[0];
      player2 = response.data[1];
      player3 = response.data[2];
      player4 = response.data[3];
      player5 = response.data[4];

      const headshot1 = player1["headShotUrl"]["S"];
      const headshot2 = player2["headShotUrl"]["S"];
      const headshot3 = player3["headShotUrl"]["S"];
      const headshot4 = player4["headShotUrl"]["S"];
      const headshot5 = player5["headShotUrl"]["S"];

      // const points = response.data["points"]["N"];
      // const headshot = response.data["headShotUrl"]["S"];
      // const result = {
      //   points: points,
      //   headshot: headshot,
      // };
      setResponseData(response.data);
    } catch (error) {
      // Handle error
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="court">
      <h1 className="title">NBA Lineup Grader</h1>
      {responseData ? (
        <h1>HELLO</h1>
      ) : (
        <div className="instructions">
          <h1>NBA Lineup Grader</h1>
          <h2>Enter a starting five:</h2>
          <NameForm onSubmit={handleSubmit} />
        </div>
      )}
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="post-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData.headshot}
            className="post-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="wing-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData.headshot}
            className="wing-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData.headshot}
            className="point-img"
            alt="headshot"
          />
        )}
      </span>
    </div>
  );
}

export default App;
