import axios from "axios";
import NameForm from "./NameInput";
import ReloadButton from "./ReloadButton";
import React, { useState } from "react"; // Import useState hook
import "./index.css";

function App() {
  const [responseData, setResponseData] = useState(null); // Declare state using useState hook

  const handleSubmit = async (inputValue) => {
    try {
      // Make API request with input value
      console.log(inputValue);

      let player1 = inputValue[0].replace(/\s+/g, "-");
      let player2 = inputValue[1].replace(/\s+/g, "-");
      let player3 = inputValue[2].replace(/\s+/g, "-");
      let player4 = inputValue[3].replace(/\s+/g, "-");
      let player5 = inputValue[4].replace(/\s+/g, "-");

      const response = await axios.get(
        `https://1h9sybsqz8.execute-api.us-east-2.amazonaws.com/dev/player-stats?pg=${player1}&sg=${player2}&sf=${player3}&pf=${player4}&c=${player5}`
      );

      for (let i = 0; i < 5; i++) {
        const player = response.data[0]["full_name"]["S"].replace(/-/g, " ");
      }

      setResponseData(response.data);
    } catch (error) {
      // Handle error
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="court">
      <h1 className="title">NBA Lineup Grader</h1>
      {responseData ? (
        <ReloadButton></ReloadButton>
      ) : (
        <div className="instructions">
          <h1>NBA Lineup Grader</h1>
          <h2>Enter a starting five:</h2>
          <NameForm onSubmit={handleSubmit} />
        </div>
      )}
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[0]["headShotUrl"]["S"]}
            className="post-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData[1]["headShotUrl"]["S"]}
            className="post-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[2]["headShotUrl"]["S"]}
            className="wing-img"
            alt="headshot"
          />
        )}
        {responseData && (
          <img
            src={responseData[3]["headShotUrl"]["S"]}
            className="wing-img"
            alt="headshot"
          />
        )}
      </span>
      <span className="img-span">
        {responseData && (
          <img
            src={responseData[4]["headShotUrl"]["S"]}
            className="point-img"
            alt="headshot"
          />
        )}
      </span>
    </div>
  );
}

export default App;
