import React, { useState } from "react";

const NameForm = () => {
  const [name1, setName1] = useState("");
  const [name2, setName2] = useState("");
  const [name3, setName3] = useState("");
  const [name4, setName4] = useState("");
  const [name5, setName5] = useState("");
  const [formError, setFormError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Perform form validation
    if (!name1 || !name2 || !name3 || !name4 || !name5) {
      setFormError("Please complete all fields.");
      return;
    }

    // Reset form error
    setFormError("");

    // You can access the entered names in the state variables (name1, name2, etc.)
    console.log("Name 1:", name1);
    console.log("Name 2:", name2);
    console.log("Name 3:", name3);
    console.log("Name 4:", name4);
    console.log("Name 5:", name5);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Point Guard"
          value={name1}
          onChange={(e) => setName1(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Shooting Guard"
          value={name2}
          onChange={(e) => setName2(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Small Forward"
          value={name3}
          onChange={(e) => setName3(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Power Forward"
          value={name4}
          onChange={(e) => setName4(e.target.value)}
        />
      </div>

      <div className="input-container">
        <input
          type="text"
          className="input-field"
          placeholder="Center"
          value={name5}
          onChange={(e) => setName5(e.target.value)}
        />
      </div>

      {formError && <p className="error-message">{formError}</p>}

      <button type="submit" className="submit-button">
        Submit
      </button>
    </form>
  );
};

export default NameForm;
